import { useEffect, useState } from "react";

export default function Practice() {
//   const [squares, setSquares] = useState(Array(9).fill(null));

  const [answerInput, setAnswerInput] = useState(0);

  answerInput = () => {
    setAnswerInput(parseInt(prompt("Please enter the number you would like to FizzBuzz up to: ")))
  };
  useEffect(answerInput, []);


  let [answerOutput, setAnswerOutput] =  "";
  answerOutput = setAnswerOutput(makeFizzBuzz(answerInput, ""));

//   function handleClick(i) {
//     const nextSquares = squares.slice();
//     nextSquares[i] = "X";
//     setSquares(nextSquares);
//   }

  return(
    <>
      <div className="practice">
        <Prompter value = {answerInput}/>
        <makeFizzBuzz number = {answerInput}/>
        <div>
          <p>{answerOutput}</p>
        </div>
        {/* <Square value = {squares[2]} onSquareClick = {() => handleClick(2)}/> */}
      </div>

    </>

  )
}

function Prompter({number}){
    <div>
      <text>{number}</text>
    </div>

}

function makeFizzBuzz({number, newString}){

  //if answer % 3 == 0 print fizz
  //if answer % 5 == 0 print buzz
  //if answer % 3 check 5 and vice versa

  for(let i = 1; i < number; i++){
    if(i%3 === 0 && i%5 === 0){
     newString += "FizzBuzz";
    }
    else if(i%3 === 0){
      newString += "Fizz";
    }
    else if(i%5 === 0){
      newString += "Buzz";
    }
    else{
      newString += i.toString();
    }
  }

  return(newString);
  // if(answer%3==0){
}

